              mode="horizontal"
              style="{lineHeight: 64px}"
              class="userInfoSlot"
              v-if="isMobile"
            >
                <a-sub-menu >
                    <span slot="title" >
                    <a-icon class="mobileList" type="menu" theme="outlined" :style="mainPage?'color: white' :'color: black'"/>
                    </span>

                    <a-menu-item key="setting:1">
                        <a href="https://github.com/MarkYnot" target="_blank">Github</a>
                      </a-menu-item>

